docortest<-function(n,r){
  #n<-length(x)
  t<-r*sqrt((n-2)/(1-r^2))
  pvalue<-2*pt(-abs(t),df=n-2)
  
  return(pvalue)
  
}




get_full_cormat<-function(data_m_fc_withfeats,targeted.index=NA,cor.method="spearman",write.files=TRUE){
  Sys.sleep(0.2)
  
  allsig_pcornetwork<-{}
  cormat<-{}
  
