      write.table(cormat,file="targetcorrelationmatrix.txt",sep="\t",row.names=TRUE)
      write.table(complete_pearsonpvalue_mat,file="targetpvalue_matrix.txt",sep="\t",row.names=TRUE)
      
    }
  }
  
  return(cormat)
}

